mapper.select:
select

from t_acc_commoninfo
where
delete_flag = 0
AND `status` = #{status,jdbcType=INTEGER}


AND segmentation = #{segmentation,jdbcType=VARCHAR}


AND ship_number = #{shipNumber,jdbcType=VARCHAR}
LIMIT #{pageNo,jdbcType=INTEGER},#{pageSize,jdbcType=INTEGER}

select

from t_acc_commoninfo
where
delete_flag = 0


    AND segmentation = #{segmentation,jdbcType=VARCHAR}


AND ship_number = #{shipNumber,jdbcType=VARCHAR}
AND update_date BETWEEN
date_format(#{startDate,jdbcType=TIMESTAMP} ,'%Y-%m-%d %H:%i:%s') and date_format(#{endDate,jdbcType=TIMESTAMP},'%Y-%m-%d %H:%i:%s')
LIMIT #{pageNo,jdbcType=INTEGER},#{pageSize,jdbcType=INTEGER}

select count(*) from t_acc_commoninfo where delete_flag = 0
AND segmentation = #{segmentation,jdbcType=VARCHAR}
AND ship_number = #{shipNumber,jdbcType=VARCHAR} AND update_date BETWEEN date_format(#{startDate,jdbcType=TIMESTAMP} ,'%Y-%m-%d %H:%i:%s') and date_format(#{endDate,jdbcType=TIMESTAMP},'%Y-%m-%d %H:%i:%s')

select count(*) from t_acc_commoninfo where delete_flag = 0
AND segmentation = #{segmentation,jdbcType=VARCHAR}
AND ship_number = #{shipNumber,jdbcType=VARCHAR} AND to_days(update_date) = to_days(now())

select
count(*)
from t_acc_commoninfo
where
delete_flag = 0


    AND segmentation = #{segmentation,jdbcType=VARCHAR}


AND ship_number = #{shipNumber,jdbcType=VARCHAR}
AND DATE_FORMAT(update_date,'%Y%m') = DATE_FORMAT(CURDATE(),'%Y%m');

select
count(*)
from t_acc_commoninfo
where
delete_flag = 0
AND `status` = #{status,jdbcType=INTEGER}


    AND segmentation = #{segmentation,jdbcType=VARCHAR}


AND ship_number = #{shipNumber,jdbcType=VARCHAR}

